      default: false,
    },
    rewardsCurrency: {
      type: String,
      default: undefined,
    },
  },
  computed: {
    mediaList() {
      return [
        {
          id: 'hkcnews',
          link: '',
        },
        {
          id: 'inmediahk',
          link: '',
        },
        {
          id: 'matters',
          link: '',
