    },
    benefitList() {
      return ['stats', 'bookmark', 'discount', 'gift'].map(id => ({
        id,
        image: getBenefitImage(`./${id}.png`),
      }));
    },
    rewards() {
      switch (this.rewardsCurrency) {
        case 'TWD':
          return '4,500,000';
        case 'HKD':
          return '1,200,000';
        case 'USD':
        default:
          return '150,000';
      }
    },
  },
};
