            </p>
          </li>
          <li class="flex flex-col items-center">
            <img
              class="w-[100px] h-[100px]"
              src="~assets/images/about/nft-book/icons/social.png"
            />
            <h4 class="font-600 mt-[3rem]">
              {{ $t('about_nft_book_section_features_social_title') }}
            </h4>
            <p class="mt-[0.5rem] text-center">
              {{ $t('about_nft_book_section_features_social_description') }}
            </p>
          </li>
        </ul>
      </div>
    </section>
  </main>
</template>

<script>
import { mapGetters } from 'vuex';

import { logTrackerEvent } from '~/util/EventLogger';
import { LIKECOIN_NFT_BOOK_INDEX_FEATURED_ITEMS } from '~/constant';

import WaveCyanBg from '~/assets/images/about/nft-book/wave-cyan.svg';
import WaveWhiteBg from '~/assets/images/about/nft-book/wave-white.svg';

export default {
  name: 'AboutNFTBookPage',
  async fetch({ store }) {
    try {
      await store.dispatch('fetchBookstoreList');
    } catch (error) {
      // eslint-disable-next-line no-console
      console.error(error);
    }
  },
  computed: {
    ...mapGetters(['nftGetBookstoreListItems']),
    bannerOverlayBG() {
      return WaveCyanBg;
    },
    storeSectionOverlayBG() {
      return WaveWhiteBg;
    },
    bookSectionClassId() {
      return LIKECOIN_NFT_BOOK_INDEX_FEATURED_ITEMS[0];
    },
    nftBooks() {
      return [
        ...this.nftBooksDisplayInFullWidth,
        ...this.nftBooksDisplayOnShelf,
      ].slice(0, 4);
    },
    nftBooksDisplayInFullWidth() {
      return this.nftGetBookstoreListItems('highlighted').filter(nft =>
        // Display books of the current locale only
        this.$i18n.locale.includes(nft.locale)
      );
    },
    nftBooksDisplayOnShelf() {
      const { locale } = this.$i18n;
      const books = [...this.nftGetBookstoreListItems('featured')];
      // Display books of the current locale first
      books.sort((a, b) => {
        const aMatchedLocale = locale.includes(a.locale);
        const bMatchedLocale = locale.includes(b.locale);
        if (aMatchedLocale && !bMatchedLocale) {
          return -1;
        }
        if (!aMatchedLocale && bMatchedLocale) {
          return 1;
        }
        return 0;
      });
      return books;
    },
  },
  mounted() {
    this.animateStars();
    this.animateBookOpen();
  },
  methods: {
    animateStars() {
      const topStars = [...this.$refs.starTop.childNodes].filter(
        el => el?.tagName === 'path'
      );
      this.$gsap.gsap.to(topStars, {
        opacity: 0,
        duration: 3,
        stagger: {
          each: 0.75,
          repeat: -1,
          yoyo: true,
        },
        ease: 'power2.inOut',
      });
      const bottomStars = [...this.$refs.starBottom.childNodes].filter(
        el => el?.tagName === 'g'
      );
      this.$gsap.gsap.to(bottomStars, {
        opacity: 0,
        duration: 3,
        stagger: {
          each: 0.75,
          repeat: -1,
          yoyo: true,
        },
        ease: 'power2.inOut',
      });
    },
    animateBookOpen() {
      const timeline = this.$gsap.gsap.timeline({
        ease: 'power2.inOut',
        delay: 2,
      });

      timeline.addLabel('slogan');

      const sloganCharacters = [...this.$refs.slogan.childNodes].filter(
        el => el?.tagName === 'g'
      );
      const sloganCharactersEmphasis = sloganCharacters.splice(4, 2);
      timeline.from(
        sloganCharacters,
        {
          opacity: 0,
          x: -50,
          duration: 3,
          stagger: {
            each: 0.35,
          },
          ease: 'power2.out',
        },
        'slogan'
      );

      const { bookCover, bookCoveGlow, bookCoveGlowBlur } = this.$refs;
      timeline.to(
        [bookCover, bookCoveGlow, bookCoveGlowBlur],
        {
          attr: {
            d: 'm1046.21,504.03l-201.91-25.67V150.79l201.91,25.67v327.58Z',
          },
          duration: 3,
          ease: 'power2.inOut',
        },
        'slogan'
      );
      timeline.to(
        this.$refs.bookGlowMask,
        {
          attr: {
            d: 'm1144.31,84.09v517.4l-300-122.72V150.79l300-66.7Z',
          },
          duration: 3,
          ease: 'power2.inOut',
        },
        'slogan'
      );
      timeline.to(
        this.$refs.doorLightJoint,
        {
          attr: {
            d: 'm1046.21,477.96h29.43v.83l-29.43,25.68v-26.51Z',
          },
          duration: 3,
          ease: 'power2.inOut',
        },
        'slogan'
      );
      timeline.to(
        this.$refs.doorLightJoint,
        {
          attr: {
            d: 'm1046.21,477.96h29.43v.83l-29.43,25.68v-26.51Z',
          },
          duration: 3,
          ease: 'power2.inOut',
        },
        'slogan'
      );

      timeline.addLabel('slogan-emphasis', 'slogan+=2');

      timeline.from(
        sloganCharactersEmphasis,
        {
          opacity: 0,
          x: -25,
          duration: 2,
          stagger: {
            each: 0.75,
          },
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );

      timeline.from(
        this.$refs.sloganEffect01,
        {
          x: 50,
          duration: 4,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect02,
        {
          x: 10,
          duration: 3,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect03,
        {
          x: 30,
          duration: 2,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect05,
        {
          x: 50,
          scale: 0.5,
          transformOrigin: 'center',
          duration: 3,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect06,
        {
          x: 20,
          duration: 4,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );

      timeline.from(
        this.$refs.sloganEffect07,
        {
          x: 50,
          transformOrigin: 'center',
          duration: 3,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );

      timeline.from(
        this.$refs.sloganEffect09,
        {
          x: 60,
          duration: 2,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect10,
        {
          x: 50,
          duration: 4,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect11,
        {
          x: 20,
          duration: 2,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );
      timeline.from(
        this.$refs.sloganEffect12,
        {
          x: 70,
          duration: 4,
          ease: 'power2.out',
        },
        'slogan-emphasis'
      );

      timeline.addLabel('post-animation', '-=3');

      timeline.to(
        this.$refs.sloganEffect04,
        {
          x: '-=50',
          opacity: 0,
          scale: 0.5,
          repeat: -1,
          duration: 4,
          transformOrigin: 'center',
          ease: 'power2.in',
        },
        'post-animation'
      );
      timeline.to(
        this.$refs.sloganEffect08,
        {
          x: '-=80',
          opacity: 0,
          scale: 0.75,
          repeat: -1,
          duration: 5,
          transformOrigin: 'center',
          ease: 'power2.in',
        },
        'post-animation'
      );
      timeline.to(
        this.$refs.sloganEffect10,
        {
          x: '-=40',
          opacity: 0,
          scale: 0.75,
          repeat: -1,
          duration: 3,
          transformOrigin: 'center',
          ease: 'power2.in',
        },
        'post-animation'
      );
    },

    handleClickBookSectionCTA() {
      logTrackerEvent(
        this,
        'AboutNFTBook',
        'AboutNFTBookBookClickCTA',
        this.bookSectionClassId,
        1
      );
    },
    onClickShelfItem(classId) {
      logTrackerEvent(
        this,
        'AboutNFTBook',
        'AboutNFTBookStoreClickItem',
        classId,
        1
      );
    },
    onClickShelfItemAvatar(classId) {
      logTrackerEvent(
        this,
        'AboutNFTBook',
        'AboutNFTBookStoreClickItemAvatar',
        classId,
        1
      );
    },
  },
};
</script>
